CREATE PROCEDURE [dbo].[xsp_application_amortization_for_download]
(
	@p_application_no nvarchar(50)
)
as
begin

	select	application_no
			,installment_no
			,convert(varchar(30), due_date, 103) 'due_date'
			,installment_principal_amount 'principal_amount'
			,installment_interest_amount 'interest_amount'
			,installment_amount
			,'81399286212download.png' 
			,'F:\PROJECT_KANTOR\V5\WebAPI\IFIN_LOS\IFIN_LOS\IFIN_LOS\Logo\81399286212download.png'
			,''
			,'9'
	from	application_amortization 
	where	application_no = @p_application_no

end ;

